// Radar chart con D3
{
  const width = 550, height = 550;
  const radius = Math.min(width, height) / 2 - 60;
  const levels = 5; // número de círculos concéntricos
  const maxValue = 3.5; // escala máxima

  const angleSlice = (Math.PI * 2) / data.length;

  const rScale = d3.scaleLinear()
    .domain([0, maxValue])
    .range([0, radius]);

  const svg = d3.create("svg")
    .attr("width", width)
    .attr("height", height);

  const g = svg.append("g")
    .attr("transform", `translate(${width/2},${height/2})`);

  // Dibujar círculos concéntricos
  for (let i = 0; i &lt;= levels; i++) {
    g.append("circle")
      .attr("r", radius/levels * i)
      .attr("fill", "none")
      .attr("stroke", "#ccc");
  }

  // Ejes radiales
  data.forEach((d, i) =&gt; {
    const angle = angleSlice * i - Math.PI/2;
    g.append("line")
      .attr("x1", 0)
      .attr("y1", 0)
      .attr("x2", rScale(maxValue) * Math.cos(angle))
      .attr("y2", rScale(maxValue) * Math.sin(angle))
      .attr("stroke", "#999");

    // Etiquetas
    g.append("text")
      .attr("x", (rScale(maxValue + 0.2)) * Math.cos(angle))
      .attr("y", (rScale(maxValue + 0.2)) * Math.sin(angle))
      .attr("dy", "0.35em")
      .style("font-size", "10px")
      .style("text-anchor", angle &gt; Math.PI/2 && angle &lt; 3*Math.PI/2 ? "end" : "start")
      .text(d.axis);
  });

  // Línea de valores
  const line = d3.lineRadial()
    .radius(d =&gt; rScale(d.value))
    .angle((d,i) =&gt; i * angleSlice)
    .curve(d3.curveLinearClosed);

  g.append("path")
    .datum(data)
    .attr("d", line)
    .attr("fill", "rgba(50,150,250,0.5)")
    .attr("stroke", "steelblue")
    .attr("stroke-width", 2);

  return svg.node();
}












